class Solution:
    # @param A : list of integers
    # @param B : integer
    # @return an integer
    def solve(self, A, B):
        freq = {}

        N = len(A)

        for ind in range(N):
            if A[ind] in freq:
                freq[A[ind]] += 1
            else:
                freq[A[ind]] = 1

        count = 0
        print(freq)
        for ind in range(N):
            k = B - A[ind]
            if k != A[ind] and k in freq and freq[k] !=0 :
                count += ((freq[k] * freq[A[ind]]) % ((10 ** 9) + 7))
                freq[k] = 0
                freq[A[ind]] = 0
            elif k == A[ind] and freq[k] > 1:
                count += (((freq[k] - 1) * freq[k]  // 2) % ((10 ** 9)+ 7))
                freq[k] = 0

        return count
